import "./Card.css";

const Card = ({ name }) => {
  return (
    <div className="card">
      <img className="card__image" src={img} alt="Image" />
      <h2 className="card__title">
        {name.charAt(0).toUpperCase() + name.slice(1)}
      </h2>
    </div>
  );
};

export default Card;
